mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney ,yonghu.yonghu_delete as yonghuDelete
FROM juanzengshuji a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and a.juanzengshuji_name like CONCAT('%',#{params.juanzengshujiName},'%')
and a.juanzengshuji_zuozhe like CONCAT('%',#{params.juanzengshujiZuozhe},'%')
and a.juanzengshuji_chubanshe like CONCAT('%',#{params.juanzengshujiChubanshe},'%')
and a.shujigoumai_types = #{params.shujigoumaiTypes}
and ( a.yonghu_id = #{params.yonghuId} )
and UNIX_TIMESTAMP(a.juanzengshuji_time) >= UNIX_TIMESTAMP(#{params.juanzengshujiTimeStart})
and UNIX_TIMESTAMP(a.juanzengshuji_time) <= UNIX_TIMESTAMP(#{params.juanzengshujiTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
